import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import { apiClient } from '../src/api/index.js';

const initialState = {
    isAuthenticated: false,
    // Initialize user if token exists, also check for saved avatar
    user: JSON.parse(localStorage.getItem('userData')) || null,
    token: localStorage.getItem('token') || '',
    loading: !!localStorage.getItem('token'),
    error: null,
}

export const login = createAsyncThunk(
    "auth/login",
    async (credentials, { rejectWithValue }) => {
        try {
            const data = await apiClient.login(credentials);
            return data;
        } catch (err) {
            return rejectWithValue(err.message);
        }
    }
);

export const logoutAsync = createAsyncThunk(
    'auth/logout',
    async (_, { rejectWithValue }) => {
        try {
            await apiClient.logout();
            return null;
        } catch (err) {
            // Even if API call fails, we should still clear local state
            console.warn('Logout API call failed, clearing local state anyway:', err);
            return null;
        }
    }
);

export const register = createAsyncThunk(
    'auth/register',
    async (userData, { rejectWithValue }) => {
        try {
            const data = await apiClient.register(userData);
            return data;
        } catch (err) {
            return rejectWithValue(err.message);
        }
    }
);

export const checkAuth = createAsyncThunk(
    'auth/checkAuth',
    async (_, { rejectWithValue }) => {
        try {
            const data = await apiClient.getCurrentUser();
            return data;
        } catch (err) {
            return rejectWithValue(err.message);
        }
    }
);

const authSlice = createSlice({
    name: "auth",
    initialState,
    reducers: {
        clearError: (state) => {
            state.error = null;
        },
        updateUserAvatar: (state, action) => {
            if (state.user) {
                state.user.avatar = action.payload;
                // Update local storage
                const savedUser = JSON.parse(localStorage.getItem('userData') || '{}');
                savedUser.avatar = action.payload;
                localStorage.setItem('userData', JSON.stringify({ ...savedUser, ...state.user }));
            }
        }
    },
    extraReducers: (builder) => {
        // Login cases
        builder
            .addCase(login.pending, (state) => {
                state.loading = true;
                state.error = null;
            })
            .addCase(login.fulfilled, (state, action) => {
                state.loading = false;
                state.token = action.payload.token;
                state.user = action.payload.user;
                // Check if there's a saved avatar for this user
                const savedAvatar = localStorage.getItem(`avatar_${action.payload.user.id}`);
                if (savedAvatar) {
                    state.user.avatar = savedAvatar;
                }
                state.isAuthenticated = true;
                localStorage.setItem('token', action.payload.token);
                localStorage.setItem('userData', JSON.stringify(state.user));
            })
            .addCase(login.rejected, (state, action) => {
                state.loading = false;
                state.error = action.payload;
            })

            // Register cases
            .addCase(register.pending, (state) => {
                state.loading = true;
                state.error = null;
            })
            .addCase(register.fulfilled, (state, action) => {
                state.loading = false;
                if (action.payload.token) {
                    state.token = action.payload.token;
                    state.user = action.payload.user;
                    state.isAuthenticated = true;
                    localStorage.setItem('token', action.payload.token);
                    localStorage.setItem('userData', JSON.stringify(state.user));
                }
                // For inactive users, just show success message, no auto-login
            })
            .addCase(register.rejected, (state, action) => {
                state.loading = false;
                state.error = action.payload;
            })

            // Check auth cases
            .addCase(checkAuth.pending, (state) => {
                state.loading = true;
            })
            .addCase(checkAuth.fulfilled, (state, action) => {
                state.loading = false;
                state.user = action.payload.user;
                // Restore avatar from local storage if available
                const savedAvatar = localStorage.getItem(`avatar_${action.payload.user.id}`);
                if (savedAvatar) {
                    state.user.avatar = savedAvatar;
                }
                state.isAuthenticated = true;
                localStorage.setItem('userData', JSON.stringify(state.user));
            })
            .addCase(checkAuth.rejected, (state, action) => {
                state.loading = false;
                state.token = '';
                state.isAuthenticated = false;
                state.user = null;
                localStorage.removeItem('token');
                localStorage.removeItem('userData');
            })

            // Logout cases
            .addCase(logoutAsync.pending, (state) => {
                state.isAuthenticated = false;
                state.user = null;
                state.error = null;
            })
            .addCase(logoutAsync.fulfilled, (state) => {
                state.token = '';
                state.isAuthenticated = false;
                state.user = null;
                state.error = null;
                localStorage.removeItem('token');
                localStorage.removeItem('userData');
            });
    },
});

export const { clearError, updateUserAvatar } = authSlice.actions;
export default authSlice.reducer;

import React, { useState, useEffect } from "react";
import AuthLayout from "./AuthLayout";
import { useNavigate, Link } from "react-router-dom";
import Button from "../UI/Button";
import { FaGraduationCap, FaChalkboardTeacher, FaShieldAlt } from "react-icons/fa";
import { useDispatch, useSelector } from 'react-redux';
import { register, clearError } from "../../../slices/AuthSlice";

export default function Register() {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { isAuthenticated, loading, error, user } = useSelector(state => state.auth);

  const [form, setForm] = useState({
    name: "",
    email: "",
    password: "",
    password_confirmation: "",
    role: "",
  });

  useEffect(() => {
    if (isAuthenticated && user) {
      // Redirect based on role
      const roleRoutes = {
        admin: '/admindashboard',
        instructor: '/teacherprofile',
        moderator: '/admindashboard', // Moderators use admin dashboard for now
        student: '/studentdashboard'
      };
      const redirectPath = roleRoutes[user.role] || '/';
      navigate(redirectPath);
    }
  }, [isAuthenticated, user, navigate]);

  useEffect(() => {
    // Clear errors when component mounts
    dispatch(clearError());
  }, [dispatch]);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  console.log(form)
  async function registerUser() {
    const response = await fetch("http://127.0.0.1:8000/api/v1/register", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(form),
    });
    const data = await response.json();
    console.log(data);
    return data.success;
  }


  const handleSubmit = async (e) => {
    e.preventDefault();

    if (form.password !== form.password_confirmation) {
      alert("Passwords don't match!");
      return;
    }

    dispatch(register(form));
  };

  const roles = [
    {
      value: "student",
      label: "Student",
      description: "Learn and enroll in courses",
      icon: <FaGraduationCap size={18} className="text-blue-600" />,
    },
    {
      value: "instructor",
      label: "Instructor",
      description: "Create and teach courses",
      icon: <FaChalkboardTeacher size={18} className="text-purple-600" />,
    },
  ];



  return (
    <AuthLayout
      title="Create account"
      subtitle="Sign up to get access to the platform"
    >
      <form onSubmit={handleSubmit} className="space-y-4">
        {error && (
          <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg">
            {error}
          </div>
        )}

        <input
          name="name"
          value={form.name}
          onChange={handleChange}
          type="text"
          placeholder="Full name"
          className="w-full border p-3 rounded-lg focus:ring-2 focus:ring-primary outline-none transition-colors duration-200"
          required
          disabled={loading}
        />
        <input
          name="email"
          value={form.email}
          onChange={handleChange}
          type="email"
          placeholder="Email address"
          className="w-full border p-3 rounded-lg focus:ring-2 focus:ring-primary outline-none transition-colors duration-200"
          required
          disabled={loading}
        />

        <div className="space-y-3">
          <label className="block text-sm font-medium text-gray-700">
            Select Your Role *
          </label>
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-3">
            {roles.map((role) => (
              <div
                key={role.value}
                className={`relative border-2 rounded-lg px-2 py-1 cursor-pointer transition-all duration-200 ${form.role === role.value
                  ? "border-primary bg-primary/5"
                  : "border-gray-200 hover:border-gray-300 hover:bg-gray-50"
                  }`}
                onClick={() => setForm({ ...form, role: role.value })}
              >
                <input
                  type="radio"
                  name="role"
                  value={role.value}
                  checked={form.role === role.value}
                  onChange={handleChange}
                  className="sr-only"
                  disabled={loading}
                />
                <div className="flex items-start space-x-3">
                  <span className="text-2xl">{role.icon}</span>
                  <div className="flex-1 min-w-0">
                    <div className="font-medium text-gray-900 text-sm">
                      {role.label}
                    </div>
                    <div className="text-[9px] text-gray-500 mt-1">
                      {role.description}
                    </div>
                  </div>
                  <div
                    className={`w-4 h-4 rounded-full border-2 flex items-center justify-center ${form.role === role.value
                      ? "border-primary bg-primary"
                      : "border-gray-300"
                      }`}
                  >
                    {form.role === role.value && (
                      <div className="w-2 h-2 rounded-full bg-white"></div>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        <input
          name="password"
          value={form.password}
          onChange={handleChange}
          type="password"
          placeholder="Password (min 8 chars)"
          className="w-full border p-3 rounded-lg focus:ring-2 focus:ring-primary outline-none transition-colors duration-200"
          required
          disabled={loading}
          minLength={8}
        />
        <input
          name="password_confirmation"
          value={form.password_confirmation}
          onChange={handleChange}
          type="password"
          placeholder="Confirm password"
          className="w-full border p-3 rounded-lg focus:ring-2 focus:ring-primary outline-none transition-colors duration-200"
          required
          disabled={loading}
        />

        <Button
          type="submit"
          text={loading ? "Creating Account..." : "Create Account"}
          className="w-full bg-primary text-white hover:bg-primary-dark transition-colors duration-200"
          disabled={loading}
        />

        <p className="text-center text-sm text-gray-500">
          Already have an account?{" "}
          <Link
            to="/login"
            className="text-primary-dark font-medium hover:underline transition-colors duration-200"
          >
            Login
          </Link>
        </p>
      </form>
    </AuthLayout>
  );
}

import React, { useEffect, useState } from "react";
import CourseCard from "./CourseCards";
import { dummyCourses } from "../../../../Data/Courses.Array";
import Button from "../../UI/Button";
import { FaGraduationCap, FaFilter, FaThLarge, FaList } from "react-icons/fa";
import Filter from "../Filter/FIlter";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { fetchCourses, selectCourses } from '../../../../slices/courseSlice';

export default function CourseGrid() {
  const [layout, setLayout] = useState("grid");
  const [showFilter, setShowFilter] = useState(false); // for mobile slide filter
  const dispatch = useDispatch();
  const course = useSelector(selectCourses);
  const loading = useSelector((state) => state.courses.loading);
  const error = useSelector((state) => state.courses.error);

  console.log("===courses", course);
  
  useEffect(() => {
    dispatch(fetchCourses());
  }, [dispatch]);

  return (
    <section className="bg-gray-50 py-10  relative overflow-hidden">
      <div className="max-w-7xl mx-auto px-4 relative">
        {/* ===== Header ===== */}
        <div className="flex justify-between items-center mb-8 lg:mx-7 mx-1 ">
          <h2 className="text-2xl font-bold text-gray-800 flex items-center gap-2">
            <FaGraduationCap className="text-primary text-4xl" />
            Available Courses
          </h2>

          <div className="flex items-center gap-3">
            {/* Filter Button */}
            <Button
              variant="outline"
              size="xs"
              icon={<FaFilter className="w-5 h-7 " />}
              onClick={() => setShowFilter(!showFilter)}
              className={`rounded-full cursor-pointer transition-all duration-300 ${showFilter ? "bg-gray-200" : "bg-white"
                }`}
            />

            {/* Layout Toggle */}
            <div className="hidden lg:block">
              <Button
                variant="outline"
                size="xs"
                icon={
                  layout === "grid" ? (
                    <FaList className="w-5 h-7 cursor-pointer" />
                  ) : (
                    <FaThLarge className="w-5 h-7 cursor-pointer" />
                  )
                }
                onClick={() =>
                  setLayout((prev) => (prev === "grid" ? "list" : "grid"))
                }
                className="p-2 rounded-full"
              />
            </div>
          </div>
        </div>

        {/* ===== Main Section ===== */}

        <div className="flex gap-8 lg:ml-5 items-start transition-all duration-500 ease-in-out">
          {/* Left: Courses */}
          <Link to="/course/:id">
            <div className="flex-1 transition-all duration-500">
              <div
                className={
                  layout === "grid"
                    ? `grid gap-6 ${showFilter
                      ? "grid-cols-1 sm:grid-cols-2 lg:grid-cols-3"
                      : "grid-cols-1 sm:grid-cols-2 lg:grid-cols-4"
                    }`
                    : "space-y-6"
                }
              >
                {course.map((course) => (
                  <CourseCard key={course.id} course={course} layout={layout} />
                ))}
              </div>
            </div>
          </Link>
          {/* Right: Filter (visible on large screen only) */}
          <div
            className={`hidden lg:block transition-all duration-500 ease-in-out transform ${showFilter
              ? "opacity-100 translate-x-0 w-80"
              : "opacity-0 -translate-x-10 w-0 overflow-hidden"
              }`}
          >
            <div className="sticky top-24">
              <Filter />
            </div>
          </div>
        </div>

      </div>

      {/*  Mobile Filter Drawer */}
      <div
        className={`fixed top-0 right-0 h-full bg-white shadow-2xl transform transition-transform duration-500 ease-in-out z-50 lg:hidden ${showFilter ? "translate-x-0" : "translate-x-full"
          }`}
      >
        <div className="p-4 h-full overflow-y-auto">
          <Filter />
        </div>
      </div>

      {/* Dark Overlay when Filter is Open */}
      {showFilter && (
        <div
          className="fixed inset-0 bg-black/70 z-40 lg:hidden"
          onClick={() => setShowFilter(false)}
        ></div>
      )}
    </section>
  );
}

import React from "react";
import { courseData } from "../../../../Data/Courses.Array";
import Button from "../../UI/Button";


export default function HeroLeft({course}) {
  console.log(course);
  console.log(course.badges
);
  return (
    <div>
      <div className="space-y-8">
        {/* Badges */}
        <div className="flex flex-wrap gap-3">
          {course.badges.map((tags, index) => (
            <span
              key={index}
              className="px-4 py-2 bg-gradient-to-r from-[#3baee9] to-[#2a9fd8] text-white text-sm font-semibold rounded-full shadow-md hover:shadow-lg transition-shadow duration-300"
            >
              {tags}
            </span>
          ))}
        </div>

        {/* Title & Tagline */}
        <div className="space-y-6">
          <h1 className="text-5xl md:text-5xl lg:text-6xl font-bold text-gray-900 leading-tight tracking-tight">
            {course.title}
          </h1>
          <p className="text-2xl text-gray-600 leading-relaxed font-light">
            {course.tagline}
          </p>
        </div>

        {/* Rating & Instructor */}
        <div className="flex flex-col sm:flex-row items-start sm:items-center gap-6">
          <div className="flex items-center gap-4">
            <div className="flex items-center bg-white px-4 py-[13px] rounded-2xl shadow-sm border border-gray-100">
              <div className="flex items-center">
                <span className="text-2xl font-bold text-[#3baee9] mr-1">
                  {course.rating}
                </span>
                <div className="flex text-yellow-400 ml-2">{"â˜…".repeat(5)}</div>
              </div>
              <div className="w-px h-6 bg-gray-200 mx-4"></div>
              <span className="text-gray-600 font-medium">
                {course.reviews} reviews
              </span>
            </div>
          </div>

          {/* Instructor */}
          <div className="flex items-center gap-3 bg-white px-3 py-2 rounded-2xl shadow-sm border border-gray-100">
            <img
              // src={course.instructor.avatar}
              alt={course.instructor.avatar}
              className="w-10 h-10 rounded-full object-cover"
            />

            <div>
              <div className="font-semibold text-gray-900">
                {course.instructor.name}
              </div>
              <div className="text-sm text-gray-500">
                {course.instructor.role}
              </div>
            </div>
          </div>
        </div>

        {/* Features Grid */}
        <div className="grid grid-cols-2 gap-4">
          {course.features.map((feature, index) => (
            <div key={index} className="flex items-center gap-3">
              <div className="w-2 h-2 bg-[#3baee9] rounded-full"></div>
              <span className="text-gray-700 font-medium">{feature}</span>
            </div>
          ))}
        </div>

        {/* Primary CTA */}
        <div className="flex flex-col sm:flex-row gap-4 pt-4">
          <Button
            text="Enroll Now"
          />
          <Button
            text="Preview Course"
            variant="outline"
          />
        </div>
      </div>
    </div>
  );
}

import React, { useState, useRef, useEffect } from "react";
import { NavLink, Link, useLocation } from "react-router-dom";
import { Play, ArrowRight, Menu, X } from "lucide-react";
import Button from "../UI/Button";
import { blogs } from "../../../Data/BlogcardsArray";
import { useSelector, useDispatch } from "react-redux";
import { logoutAsync } from "../../../slices/AuthSlice";

const Navbar = () => {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [activeLevel, setActiveLevel] = useState("beginner");
  const [activeCourse, setActiveCourse] = useState(null);
  const coursesDropdownRef = useRef(null);
  const location = useLocation();

  const { isAuthenticated, user } = useSelector(state => state.auth);
  const dispatch = useDispatch();

  const handleLogout = () => {
    dispatch(logoutAsync());
  };

  // Role-based dashboard routes
  const getDashboardRoute = (role) => {
    const routes = {
      admin: '/admindashboard',
      instructor: '/teachermaindashboard',
      moderator: '/admindashboard',
      student: '/studentdashboard'
    };
    return routes[role] || '/studentdashboard';
  };

  useEffect(() => {
    // Any navbar-specific initialization if needed
  }, []);


  const courseToSubcourses = {
    "html-basics": "html-basics-subcourses",
    "intro-css": "intro-css-subcourses",
    "js-basics": "js-basics-subcourses",
    "git-basics": "html-basics-subcourses",
    "react-fundamentals": "intro-css-subcourses",
    "nodejs-basics": "js-basics-subcourses",
    "tailwind-projects": "intro-css-subcourses",
    "mongodb-basics": "js-basics-subcourses",
    "full-stack": "full-stack-subcourses",
    "api-integration": "full-stack-subcourses",
    "nextjs-advanced": "full-stack-subcourses",
    microservices: "full-stack-subcourses",
  };

  const courseLevels = {
    beginner: [
      { id: "html-basics", name: "HTML Basics" },
      { id: "intro-css", name: "Intro to CSS" },
      { id: "js-basics", name: "JavaScript Basics" },
      { id: "git-basics", name: "Git Basics" },
    ],
    intermediate: [
      { id: "react-fundamentals", name: "React Fundamentals" },
      { id: "nodejs-basics", name: "Node.js Basics" },
      { id: "tailwind-projects", name: "Tailwind CSS Projects" },
      { id: "mongodb-basics", name: "MongoDB Basics" },
    ],
    advanced: [
      { id: "full-stack", name: "Full Stack Development" },
      { id: "api-integration", name: "API Integration" },
      { id: "nextjs-advanced", name: "Next.js Advanced" },
      { id: "microservices", name: "Microservices Architecture" },
    ],
  };

  const subcourses = {
    "html-basics-subcourses": [
      { name: "HTML Structure & Syntax" },
      { name: "Forms & Inputs" },
      { name: "Semantic HTML" },
      { name: "HTML5 Features" },
    ],
    "intro-css-subcourses": [
      { name: "CSS Selectors" },
      { name: "Box Model" },
      { name: "Flexbox" },
      { name: "CSS Grid" },
    ],
    "js-basics-subcourses": [
      { name: "Variables & Data Types" },
      { name: "Functions & Scope" },
      { name: "DOM Manipulation" },
      { name: "Event Handling" },
    ],
    "full-stack-subcourses": [
      { name: "Frontend Development" },
      { name: "Backend Development" },
      { name: "Database Integration" },
      { name: "Deployment Strategies" },
    ],
  };

  const handleLevelHover = (level) => {
    setActiveLevel(level);
    setActiveCourse(null);
  };

  const handleCourseHover = (courseId) => {
    setActiveCourse(courseId);
  };

  const handleMobileMenuToggle = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  const handleNavLinkClick = () => {
    setMobileMenuOpen(false);
    setActiveCourse(null);
    setActiveLevel("beginner");
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        mobileMenuOpen &&
        !event.target.closest("#mobile-menu") &&
        !event.target.closest("#menu-btn")
      ) {
        setMobileMenuOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [mobileMenuOpen]);

  const MobileCoursesDropdown = () => {
    const [mobileActiveLevel, setMobileActiveLevel] = useState("beginner");
    const [mobileActiveCourse, setMobileActiveCourse] = useState(null);

    return (
      <div className="space-y-4">
        <div>
          <h3 className="font-semibold text-gray-900 mb-3 text-sm uppercase tracking-wide">
            Course Levels
          </h3>
          <div className="grid grid-cols-3 gap-2">
            {["beginner", "intermediate", "advanced"].map((level) => (
              <button
                key={level}
                className={`p-3 rounded-lg transition-all duration-200 border text-sm font-medium capitalize ${mobileActiveLevel === level
                  ? "bg-primary-dark text-white border-primary-dark"
                  : "bg-white text-gray-700 border-gray-300 hover:border-primary-dark hover:text-primary-dark"
                  }`}
                onClick={() => {
                  setMobileActiveLevel(level);
                  setMobileActiveCourse(null);
                }}
              >
                {level}
              </button>
            ))}
          </div>
        </div>

        <div>
          <h3 className="font-semibold text-gray-900 mb-3 text-sm uppercase tracking-wide">
            Courses
          </h3>
          <div className="space-y-2">
            {courseLevels[mobileActiveLevel]?.map((course) => (
              <button
                key={course.id}
                className={`w-full text-left p-3 rounded-lg border transition-all duration-200 text-sm font-medium ${mobileActiveCourse === course.id
                  ? "bg-primary-dark text-white border-primary-dark"
                  : "bg-white text-gray-700 border-gray-300 hover:border-primary-dark hover:text-primary-dark"
                  }`}
                onClick={() =>
                  setMobileActiveCourse(
                    mobileActiveCourse === course.id ? null : course.id
                  )
                }
              >
                {course.name}
              </button>
            ))}
          </div>
        </div>

        {mobileActiveCourse && (
          <div>
            <h3 className="font-semibold text-gray-900 mb-3 text-sm uppercase tracking-wide">
              Lessons
            </h3>
            <div className="space-y-2">
              {subcourses[courseToSubcourses[mobileActiveCourse]]?.map(
                (subcourse, index) => (
                  <div
                    key={index}
                    className="p-3 rounded-lg bg-blue-50 border border-blue-200 transition-all duration-200 flex items-center"
                  >
                    <div className="w-8 h-8 bg-primary-dark rounded-lg flex items-center justify-center mr-3">
                      <Play className="w-4 h-4 text-white" />
                    </div>
                    <span className="text-sm font-medium text-gray-700">
                      {subcourse.name}
                    </span>
                  </div>
                )
              )}
            </div>
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="w-full bg-white shadow-lg border-b border-gray-100 sticky top-0 z-50">
      {" "}
      <header className="max-w-7xl mx-auto flex justify-between items-center lg:px-8 px-4 lg:py-2 py-3 bg-white relative z-50">
        {" "}
        <div className="flex items-center">
          {" "}
          <NavLink
            to="/"
            className="text-2xl font-bold text-gray-900 hover:no-underline tracking-tight"
            onClick={handleNavLinkClick}
          >
            Bit<span className="text-primary-dark">CoderLabs</span>{" "}
          </NavLink>{" "}
        </div>
        <nav className="hidden lg:flex items-center space-x-1">
          <div className="group relative px-3 py-2">
            <NavLink
              to="/"
              className={({ isActive }) =>
                `font-medium transition-all duration-300 relative py-2 px-1 ${isActive
                  ? "text-primary-dark"
                  : "text-gray-700 hover:text-primary-dark"
                }`
              }
            >
              Home
              <span
                className={`absolute bottom-0 left-0 w-full h-0.5 bg-primary-dark transition-all duration-300 ${location.pathname === "/"
                  ? "scale-x-100"
                  : "scale-x-0 group-hover:scale-x-100"
                  }`}
              ></span>
            </NavLink>
          </div>

          <div
            className="courses-dropdown group relative px-3 py-2"
            ref={coursesDropdownRef}
          >
            <NavLink
              to="/courses"
              className={({ isActive }) =>
                `font-medium transition-all duration-300 relative py-2 px-1 flex items-center gap-1 ${isActive
                  ? "text-primary-dark"
                  : "text-gray-700 hover:text-primary-dark"
                }`
              }
            >
              Courses
              <svg
                className="w-3 h-3 transition-transform duration-300 group-hover:rotate-180"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M19 9l-7 7-7-7"
                />
              </svg>
            </NavLink>

            <div className="absolute left-1/2 transform -translate-x-1/2 top-full mt-1 bg-white rounded-xl shadow-2xl border border-gray-200 z-50 w-[800px] opacity-0 invisible -translate-y-4 transition-all duration-300 group-hover:opacity-100 group-hover:visible group-hover:translate-y-0">
              {/* Level / Course / Subcourse columns */}
              <div className="flex h-[400px]">
                <div className="w-1/4 bg-white p-6 border-r border-gray-200 overflow-y-auto rounded-l-xl">
                  <h3 className="font-semibold text-gray-900 mb-4 text-sm uppercase tracking-wide">
                    Course Levels
                  </h3>
                  <ul className="space-y-1">
                    {["beginner", "intermediate", "advanced"].map((level) => (
                      <li key={level} className="level-item group/level">
                        <button
                          className={`w-full text-left p-3 rounded-lg transition-all duration-200 flex items-center justify-between border ${activeLevel === level
                            ? "bg-primary-dark text-white shadow-md"
                            : "text-gray-700 border-transparent hover:bg-white hover:border-blue-200 hover:text-blue-600 hover:shadow-sm"
                            }`}
                          onMouseEnter={() => handleLevelHover(level)}
                        >
                          <span className="capitalize font-medium text-sm">
                            {level}
                          </span>
                          <ArrowRight className="w-3 h-3 opacity-0 group-hover/level:opacity-100 transition-opacity duration-200" />
                        </button>
                      </li>
                    ))}
                  </ul>
                </div>

                <div className="w-2/5 p-6 border-r border-gray-200 overflow-y-auto">
                  <h3 className="font-semibold text-gray-900 mb-4 text-sm uppercase tracking-wide">
                    Courses
                  </h3>
                  <div className="space-y-1">
                    {courseLevels[activeLevel]?.map((course) => (
                      <div key={course.id} className="course-item group/course">
                        <button
                          className="w-full text-left p-3 rounded-lg text-gray-700 border border-transparent hover:bg-primary-dark hover:text-white cursor-pointer hover:border-white transition-all duration-200 flex items-center justify-between"
                          onMouseEnter={() => handleCourseHover(course.id)}
                        >
                          <span className="font-medium text-sm">
                            {course.name}
                          </span>
                          <ArrowRight className="w-3 h-3 opacity-0 group-hover/course:opacity-100 transition-opacity duration-200" />
                        </button>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="w-2/5 p-6 overflow-y-auto rounded-r-xl">
                  <h3 className="font-semibold text-gray-900 mb-4 text-sm uppercase tracking-wide">
                    Lessons
                  </h3>
                  {!activeCourse ? (
                    <div className="text-gray-400 text-sm italic text-center py-8">
                      Select a course to view lessons
                    </div>
                  ) : (
                    <div className="space-y-2">
                      {subcourses[courseToSubcourses[activeCourse]]?.map(
                        (subcourse, index) => (
                          <div
                            key={index}
                            className="p-3 rounded-lg hover:bg-blue-50 transition-all duration-200 flex items-center cursor-pointer group/lesson border border-transparent hover:border-blue-200"
                          >
                            <div className="w-8 h-8 bg-blue-100 rounded-lg flex items-center justify-center mr-3 group-hover/lesson:bg-primary-dark transition-colors duration-200">
                              <Play className="w-4 h-4 text-primary-dark group-hover/lesson:text-white transition-colors duration-200" />
                            </div>
                            <span className="text-sm font-medium text-gray-700 group-hover/lesson:text-primary-dark transition-colors duration-200">
                              {subcourse.name}
                            </span>
                          </div>
                        )
                      )}
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>

          {["about", "Blog", "prices", "contact"].map((item) => (
            <div key={item} className="group relative px-3 py-2">
              <NavLink
                to={`/${item}`}
                className={({ isActive }) =>
                  `font-medium transition-all duration-300 relative py-2 px-1 ${isActive
                    ? "text-primary-dark"
                    : "text-gray-700 hover:text-primary-dark"
                  }`
                }
              >
                {item.charAt(0).toUpperCase() + item.slice(1)}
                <span
                  className={`absolute bottom-0 left-0 w-full h-0.5 bg-primary-dark transition-all duration-300 ${location.pathname === `/${item}`
                    ? "scale-x-100"
                    : "scale-x-0 group-hover:scale-x-100"
                    }`}
                ></span>
              </NavLink>
            </div>
          ))}
        </nav>
        {/* Desktop Auth Buttons */}
        <div className="hidden lg:flex items-center gap-3">
          {isAuthenticated ? (
            <>
              <button onClick={handleLogout}>
                <Button
                  text="Logout"
                  variant="squarefull"
                  className="w-full justify-center"
                  size="sm"
                />
              </button>
              <Link to={getDashboardRoute(user?.role)} onClick={handleNavLinkClick} className="relative">
                <div className="rounded-full w-8 h-8 bg-primary text-white text-center flex justify-center overflow-hidden items-center font-semibold">
                  {user?.avatar && user.avatar.startsWith('data:') ? (
                    <img
                      src={user.avatar}
                      alt="Profile"
                      className="w-full h-full object-cover"
                    />
                  ) : (
                    user?.name?.charAt(0)?.toUpperCase() || 'U'
                  )}
                </div>
              </Link>
            </>
          ) : (
            <>
              <Link to="/register" className="block" onClick={handleNavLinkClick}>
                <Button
                  text="Sign In"
                  variant="squarefull"
                  className="w-full justify-center"
                />
              </Link>
              <Link to="/login" className="block" onClick={handleNavLinkClick}>
                <Button
                  text="Log In"
                  variant="squarefull"
                  className="w-full justify-center"
                />
              </Link>
            </>
          )}
        </div>
        {/* Mobile Menu Button */}
        <div className="lg:hidden">
          <button
            id="menu-btn"
            className="text-gray-700 hover:text-blue-600 transition-colors duration-300 p-2 rounded-lg hover:bg-blue-50"
            onClick={handleMobileMenuToggle}
          >
            {mobileMenuOpen ? (
              <X className="w-6 h-6" />
            ) : (
              <Menu className="w-6 h-6" />
            )}
          </button>
        </div>
      </header>
      {/* Mobile Menu */}
      <div
        id="mobile-menu"
        className={`lg:hidden fixed top-0 left-0 right-0 bg-white shadow-xl z-40 transition-transform duration-300 ease-in-out overflow-y-auto max-h-[calc(100vh-64px)] ${mobileMenuOpen ? "translate-y-0" : "-translate-y-full"
          }`}
        style={{ marginTop: "64px" }}
      >
        <div className="max-w-7xl mx-auto px-4 py-6">
          <nav className="space-y-4 mb-6">
            <NavLink
              to="/"
              className={({ isActive }) =>
                `block py-3 px-4 rounded-lg font-medium transition-all duration-200 border ${isActive
                  ? "bg-primary-dark text-white border-primary-dark"
                  : "text-gray-700 border-gray-200 hover:bg-gray-50 hover:border-primary-dark"
                }`
              }
              onClick={handleNavLinkClick}
            >
              Home
            </NavLink>

            <div className="py-3 px-4 rounded-lg border border-gray-200">
              <MobileCoursesDropdown />
            </div>

            {["about", "Blog", "prices", "contact"].map((item) => (
              <NavLink
                key={item}
                to={`/${item}`}
                className={({ isActive }) =>
                  `block py-3 px-4 rounded-lg font-medium transition-all duration-200 border ${isActive
                    ? "bg-primary-dark text-white border-primary-dark"
                    : "text-gray-700 border-gray-200 hover:bg-gray-50 hover:border-primary-dark"
                  }`
                }
                onClick={handleNavLinkClick}
              >
                {item.charAt(0).toUpperCase() + item.slice(1)}
              </NavLink>
            ))}
          </nav>

          <div className="space-y-3 pt-4 border-t border-gray-200 flex items-center justify-end gap-3">
            {isAuthenticated ? (
              <div className="flex items-center gap-3">
                <Link to={getDashboardRoute(user?.role)} onClick={handleNavLinkClick} className="relative">
                  <div className="rounded-full w-8 h-8 bg-primary text-white text-center flex justify-center overflow-hidden items-center font-semibold">
                    {user?.avatar && user.avatar.startsWith('data:') ? (
                      <img
                        src={user.avatar}
                        alt="Profile"
                        className="w-full h-full object-cover"
                      />
                    ) : (
                      user?.name?.charAt(0)?.toUpperCase() || 'U'
                    )}
                  </div>
                </Link>
                <button onClick={handleLogout} className="text-sm text-gray-600 hover:text-gray-800">
                  Logout
                </button>
              </div>
            ) : (
              <>
                <Link to="/register" className="block" onClick={handleNavLinkClick}>
                  <Button
                    text="Sign In"
                    variant="squarefull"
                    className="w-full justify-center"
                  />
                </Link>
                <Link to="/login" className="block" onClick={handleNavLinkClick}>
                  <Button
                    text="Log In"
                    variant="squarefull"
                    className="w-full justify-center"
                  />
                </Link>
              </>
            )}
          </div>

        </div>
      </div>
      {mobileMenuOpen && (
        <div
          className="fixed inset-0 bg-black/30 backdrop-blur-sm z-30 transition-opacity duration-300 lg:hidden"
          onClick={() => setMobileMenuOpen(false)}
        ></div>
      )}
    </div>
  );
};

export default Navbar;

import React from "react";
import { FaArrowRight } from "react-icons/fa";

export default function CategoriesCards({
  title,
  desc,
  count,
  icon: Icon,
  color,
}) {
  return (
    <div className="group relative bg-white border border-gray-100 rounded-2xl p-6 my-10 shadow-sm hover:shadow-xl hover:-translate-y-2 transition-all duration-300 cursor-pointer flex flex-col justify-between h-auto md:h-[320px]">
      {/* Gradient Hover Background */}
      <div
        className={`absolute inset-0 bg-gradient-to-r ${color} opacity-0 group-hover:opacity-10 rounded-2xl transition-all`}
      />

      {/* Icon */}
      <div 
        className={`w-14 h-14 mb-5 rounded-2xl bg-gradient-to-r ${color} flex items-center justify-center text-white text-xl group-hover:scale-110 transition-transform`}
      >
        <Icon />
      </div>

      {/* Content */}
      <div className="flex-1">
        <h3 className="font-bold text-lg mb-2">{title}</h3>
        <p className="text-gray-600 text-sm ">{desc}</p>
      </div>

      {/* Footer */}
      <div className="flex justify-between items-center border-t border-gray-100 pt-3">
        <span className="text-sm text-gray-500">{count} Courses</span>
        <FaArrowRight className="text-gray-400 group-hover:text-gray-800 transition-all duration-200" />
      </div>
    </div>
  );
}

// src/Component/Home/TopInstructors.jsx
import React from "react";
import {
  FaStar,
  FaUsers,
  FaPlayCircle,
  // FaLinkedin,
  // FaTwitter,
  // FaGithub,
} from "react-icons/fa";
import { socialIcons } from "../../../Data/Array";
import SectionHeader from "../UI/SectionHeader";
import { instructors } from "../../../Data/Array";
import Button from "../UI/Button";

export default function TopInstructors() {
  return (
    <section className="py-20 bg-gradient-to-br from-gray-50 to-indigo-50/20 place-items-center">
      <div className="max-w-7xl mx-auto ">
        {/* Section Header */}
        <SectionHeader
          title="TOP INSTRUCTORS"
          subtitle={
            <>
              <span className="text-transparent bg-clip-text bg-gradient-to-r from-[#3baee9] to-[#2a9fd8]">
                Learn from
              </span>{" "}
              <span className="text-transparent bg-clip-text bg-gradient-to-r from-[#2a9fd8] to-[#3baee9]">
                Industry Experts
              </span>
            </>
          }
          description="Get mentored by professionals who are shaping the future of
            technology"
        />

        {/* Instructors Container */}
        <div className="space-y-20">
          {instructors.map((instructor) => (
            <div
              key={instructor.id}
              className={`flex flex-col ${
                instructor.alignment === "left"
                  ? "lg:flex-row"
                  : "lg:flex-row-reverse"
              } items-center gap-12 lg:gap-20`}
            >
              {/* Image Section  */}
              <div className="flex-1">
                <div className="relative max-w-lg w-full mx-auto lg:mx-0">
                  {/* Main Image Container */}
                  <div className="relative rounded-2xl overflow-hidden group">
                    <img
                      src={instructor.image}
                      alt={instructor.name}
                      className="w-full h-[380px] object-cover object-top transition-transform duration-700 group-hover:scale-105"
                    />

                    {/* Overlay Gradient */}
                    <div className="absolute inset-0 bg-gradient-to-t from-black/10 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
                  </div>

                  {/* Experience Badge */}
                  <div className="absolute -top-4 -left-4 bg-white rounded-2xl shadow-xl border border-gray-100 px-5 py-3">
                    <div className="text-center">
                      <div className="text-lg font-bold text-gray-900">
                        {instructor.experience}
                      </div>
                      <div className="text-xs text-gray-500">Experience</div>
                    </div>
                  </div>

                  {/* Social Links - Minimal */}
                  <div className="absolute -bottom-4 -right-4 bg-white rounded-2xl shadow-xl border border-gray-100 px-4 py-3">
                    <div className="flex gap-2">
                      {socialIcons.map(({ icon: Icon, color, key }) => (
                        <a
                          key={key}
                          href={instructor.social[key]}
                          className={`w-8 h-8 rounded-lg flex items-center justify-center text-gray-600 transition-colors duration-300 ${color}`}
                          target="_blank"
                          rel="noopener noreferrer"
                        >
                          <Icon className="text-sm" />
                        </a>
                      ))}
                    </div>
                  </div>
                </div>
              </div>

              {/* Content Section */}
              <div className="flex-1 max-w-2xl mx-4 lg:mx-0">
                <div className="space-y-6">
                  {/* Name & Title */}
                  <div>
                    <h3 className="text-3xl font-bold text-gray-900 mb-3">
                      {instructor.name}
                    </h3>
                    <p className="text-lg text-primary font-semibold mb-4">
                      {instructor.title}
                    </p>
                    <p className="text-gray-600 leading-relaxed text-justify">
                      {instructor.description}
                    </p>
                  </div>

                  {/* Stats */}
                  <div className="grid grid-cols-3 gap-6 ">
                    <div className="">
                      <div className="flex items-center justify-start gap-1 mb-2">
                        <FaStar className="text-yellow-400 text-lg" />
                        <span className="text-2xl font-bold text-gray-900">
                          {instructor.rating}
                        </span>
                      </div>
                      <span className="text-gray-500 text-sm ml-4">Rating</span>
                    </div>

                    <div className="text-center">
                      <div className="flex items-center justify-center gap-1 mb-2">
                        <FaUsers className="text-primary text-lg" />
                        <span className="text-2xl font-bold text-gray-900">
                          {(instructor.students / 1000).toFixed(0)}K+
                        </span>
                      </div>
                      <span className="text-gray-500 text-sm">Students</span>
                    </div>

                    <div className="text-center">
                      <div className="mb-2">
                        <span className="text-2xl font-bold text-gray-900">
                          {instructor.courses}+
                        </span>
                      </div>
                      <span className="text-gray-500 text-sm">Courses</span>
                    </div>
                  </div>

                  {/* Specialization */}
                  <div>
                    <h4 className="text-sm font-semibold text-gray-500 uppercase tracking-wide mb-3">
                      Specializes In
                    </h4>
                    <div className="flex flex-wrap gap-2">
                      {instructor.specialization.map((skill, index) => (
                        <span
                          key={index}
                          className="bg-gray-100 text-gray-700 px-3 py-2 rounded-lg font-medium text-sm hover:bg-indigo-50 hover:text-indigo-700 transition-colors duration-300"
                        >
                          {skill}
                        </span>
                      ))}
                    </div>
                  </div>

                  {/* CTA Buttons */}
                  <div className=" lg:mt-2 mt-8">
                    <div className=" hidden sm:inline-flex items-center gap-8">
                      <Button text=" Start Free Trial "  rounded="lg" size="md" />
                      <Button
                        text="Schedule Demo"
                        variant="outline"
                        size="md"
                        rounded="lg"
                      />
                    </div>
                    <div className="sm:hidden inline-flex items-center gap-4">
                      <Button text=" Start Free Trial " size="sm" rounded="lg" />
                      <Button
                        text="Schedule Demo"
                        variant="outline"
                        size="sm"
                        rounded="lg"
                      />
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Bottom CTA */}
        <div className="text-center mt-16 place-items-center">
          <Button text="Browse Course " size="lg" />
        </div>
      </div>
    </section>
  );
}

