// src/components/courses/CoursesPage.jsx
import React, { useState, useEffect } from "react";
import { useSelector } from 'react-redux';
import AddCourseForm from "./AddCourseFrom";
import Button from "../../Component/UI/Button";
import { Edit2, RefreshCw, Trash2 } from "lucide-react";
import { apiClient } from '../../../src/api/index.js';
import { API_ORIGIN } from "../../api/index.js";


export default function CoursesPage() {
  const [courses, setCourses] = useState([]);
  const [pagination, setPagination] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [perPage, setPerPage] = useState(12);
  const [openForm, setOpenForm] = useState(false);
  const [editingCourse, setEditingCourse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [lastUpdate, setLastUpdate] = useState(Date.now());
  const [isOperationInProgress, setIsOperationInProgress] = useState(false);

  const { user, isAuthenticated } = useSelector(state => state.auth);

  const resolveImageUrl = (image) => {
    if (!image) return null;
    if (typeof image !== "string") return null;
    if (image.startsWith("http://") || image.startsWith("https://")) return image;
    if (image.startsWith("/")) return `${API_ORIGIN}${image}`;
    return `${API_ORIGIN}/${image}`;
  };

  // Fetch courses from API
  const fetchCourses = async (page = 1, showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const response = await apiClient.getCourses({
        page: page,
        per_page: perPage
      });

      // Handle paginated response from Laravel API
      if (response && response.data) {
        setCourses(response.data);
        setPagination(response.meta || null);

      } else {
        // Fallback for non-paginated response
        setCourses([]);
        setPagination(null);

      }
      setLastUpdate(Date.now());
    } catch (error) {
      console.error('Error fetching courses:', error);
      // Keep existing data on error to avoid UI flicker
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  useEffect(() => {
    fetchCourses(currentPage, true);
  }, [currentPage, perPage]);

  // Disabled real-time polling for admin dashboard to prevent conflicts with pagination
  // Admins can manually refresh using the refresh button if needed

  // Force refresh function for manual updates
  const forceRefresh = () => {
    fetchCourses(currentPage, true);
  };

  const handleSubmitCourse = async (courseData) => {
    try {
      setLoading(true);
      setIsOperationInProgress(true);
      console.log('Submitting course data:', courseData);

      // Check if courseData is FormData (contains file upload)
      const isFormData = courseData instanceof FormData;

      let submitData;
      if (isFormData) {
        console.log('Received FormData, using directly for API call');
        submitData = courseData;

        // Add any missing fields that might not be in FormData
        if (!submitData.has('is_featured')) submitData.append('is_featured', '0');
        if (!submitData.has('is_active')) submitData.append('is_active', '1');
        if (!submitData.has('features')) submitData.append('features', '[]');
        if (!submitData.has('tags')) submitData.append('tags', '[]');
      } else {
        // Handle regular object data (no file upload)
        submitData = {
          ...courseData,
          // Handle optional text fields - convert empty strings to null
          short_description: courseData.short_description || null,
          image: courseData.image || null,
          video_url: courseData.video_url || null,
          duration: courseData.duration || null,
          original_price: courseData.original_price ? parseFloat(courseData.original_price) : null,
          features: courseData.features ? JSON.stringify(courseData.features) : "[]",
          tags: courseData.tags ? JSON.stringify(courseData.tags) : "[]",
          // Convert numeric fields
          price: parseFloat(courseData.price) || 0,
          rating: courseData.rating ? parseFloat(courseData.rating) : 0,
          reviews_count: courseData.reviews_count ? parseInt(courseData.reviews_count) : 0,
          students_count: courseData.students_count ? parseInt(courseData.students_count) : 0,
          // Convert IDs to integers (ensure they're not null for required fields)
          instructor_id: courseData.instructor_id ? parseInt(courseData.instructor_id) : undefined,
          category_id: courseData.category_id ? parseInt(courseData.category_id) : undefined,
          // Boolean fields
          is_featured: Boolean(courseData.is_featured),
          is_active: Boolean(courseData.is_active),
        };
      }

      console.log('Prepared data for API:', submitData);

      let response;
      if (editingCourse) {
        // Update existing course
        console.log('Updating course:', editingCourse.id);
        response = await apiClient.updateCourse(editingCourse.id, submitData);

        // Update the course in local state with server response for accurate data
        setCourses(prevCourses =>
          prevCourses.map(course =>
            course.id === editingCourse.id
              ? response.data // Use server response which has correct structure
              : course
          )
        );
      } else {
        // Check if user is authenticated and is admin
        if (!isAuthenticated) {
          throw new Error('You must be logged in to create courses.');
        }
        if (user?.role !== 'admin') {
          throw new Error('Only administrators can create courses.');
        }

        // For FormData, validation is handled by Laravel on the backend
        if (!isFormData) {
          // Validate required fields before creating (only for regular object data)
          const requiredFields = ['title', 'slug', 'description', 'instructor_id', 'category_id', 'price', 'level', 'language'];
          const missingFields = requiredFields.filter(field => !submitData[field] || submitData[field] === '' || submitData[field] === null || submitData[field] === undefined);

          if (missingFields.length > 0) {
            throw new Error(`Missing required fields: ${missingFields.join(', ')}. Please fill all required fields.`);
          }

          // Check if instructor_id and category_id are valid numbers
          if (isNaN(submitData.instructor_id) || isNaN(submitData.category_id)) {
            throw new Error('Please select a valid instructor and category.');
          }
        }

        console.log('Creating new course with data:', submitData);
        response = await apiClient.createCourse(submitData);
        console.log('Create response:', response);

        // Add new course to the beginning of the list
        if (response.data) {
          console.log('Adding new course to state:', response.data);
          setCourses(prevCourses => [response.data, ...prevCourses]);
          console.log('Updated courses state with new course');
        }
      }

      console.log('API response:', response);
      // Do a final refresh to ensure consistency
      setTimeout(() => fetchCourses(currentPage, false), 1000);
      setOpenForm(false);
      setEditingCourse(null);
    } catch (error) {
      console.error('Error saving course:', error);
      console.error('Error details:', error.message);
      console.error('Error response:', error.response?.data);
      console.error('Error status:', error.response?.status);
      console.error('Error headers:', error.response?.headers);

      // Show more specific error message
      const errorMessage = error.response?.data?.message ||
        error.response?.data?.error ||
        error.message ||
        'Failed to save course. Please try again.';
      alert(`Error: ${errorMessage}`);
    } finally {
      setLoading(false);
      setIsOperationInProgress(false);
    }
  };

  const handleEditCourse = (course) => {
    setEditingCourse(course);
    setOpenForm(true);
  };

  const handleDeleteCourse = async (courseId) => {
    if (window.confirm("Are you sure you want to delete this course?")) {
      try {
        setIsOperationInProgress(true);
        await apiClient.deleteCourse(courseId);
        setCourses((prev) => prev.filter((course) => course.id !== courseId));
        // Do a final refresh to ensure consistency
        setTimeout(() => fetchCourses(currentPage, false), 1000);
      } catch (error) {
        console.error('Error deleting course:', error);
        alert('Failed to delete course. Please try again.');
      } finally {
        setIsOperationInProgress(false);
      }
    }
  };





  const handlePageChange = (page) => {
    if (page !== currentPage && page >= 1 && page <= (pagination?.last_page || 1)) {
      setCurrentPage(page);
    }
  };

  const handlePerPageChange = (newPerPage) => {
    setPerPage(newPerPage);
    setCurrentPage(1); // Reset to first page when changing per page
  };



  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-dark"></div>
      </div>
    );
  }

  return (
    <div className=" bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">
            Courses Management
          </h1>
          <p className="text-gray-600 mt-2">
            Manage and organize your course offerings
          </p>
        </div>
        <div className="flex gap-3">
          <button
            onClick={forceRefresh}
            disabled={loading}
            className="flex items-center gap-2 px-4 py-1 cursor-pointer border border-gray-300 text-gray-700 hover:bg-gray-50 rounded-lg font-medium transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
            title="Refresh courses"
          >
            <RefreshCw size={18} className={loading ? 'animate-spin' : ''} />
            Refresh
          </button>
          <Button
            variant="primary"
            text="+ Add New Course"
            onClick={() => setOpenForm(true)}
            className="bg-primary hover:bg-primary-dark text-white px-4 py-1 rounded-lg font-semibold transition-colors duration-200"
          />
        </div>
      </div>

      {/* Add Course Form Modal */}
      {openForm && (
        <div className="fixed inset-0 bg-black/70  flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-xl max-w-2xl w-full max-h-[90vh] overflow-y-scroll no-scrollbar">
            <AddCourseForm
              onClose={() => {
                setOpenForm(false);
                setEditingCourse(null);
              }}
              onSubmit={handleSubmitCourse}
              initialData={editingCourse}
            />
          </div>
        </div>
      )}

      {/* Courses Table */}
      <div className="space-y-6">
        <div className="flex justify-between items-center">
          <h2 className="text-xl font-semibold text-gray-800">
            All Courses ({pagination?.total || courses.length})
          </h2>
        </div>

        <div className="bg-white border border-gray-200 rounded-xl overflow-hidden">
          <div className="overflow-x-auto">
            <table className="min-w-full text-sm">
              <thead className="bg-primary  text-white">
                <tr>
                  <th className="text-left font-semibold px-4 py-3">Course</th>
                  <th className="text-left font-semibold px-4 py-3">Category</th>
                  <th className="text-left font-semibold px-4 py-3">Instructor</th>
                  <th className="text-left font-semibold px-4 py-3">Price</th>
                  <th className="text-left font-semibold px-4 py-3">Level</th>
                  <th className="text-left font-semibold px-4 py-3">Status</th>
                  <th className="text-right font-semibold px-4 py-3">Actions</th>
                </tr>
              </thead>

              <tbody className="divide-y divide-gray-200">
                {courses.map((course) => (
                  <tr key={course.id} className="hover:bg-gray-50">
                    <td className="px-4 py-3">
                      <div className="flex items-center gap-3">
                        <img
                          src={resolveImageUrl(course.image) || 'https://via.placeholder.com/80x60/4f46e5/ffffff?text=Img'}
                          alt={course.title}
                          className="w-16 h-12 rounded-md object-cover border border-gray-200 shrink-0"
                          onError={(e) => {
                            e.target.src = 'https://via.placeholder.com/80x60/4f46e5/ffffff?text=Img';
                          }}
                        />
                        <div className="min-w-0">
                          <div className="font-semibold text-gray-900 truncate max-w-[18rem]">
                            {course.title}
                          </div>
                          <div className="text-gray-500 truncate max-w-[18rem]">
                            {course.slug}
                          </div>
                        </div>
                      </div>
                    </td>

                    <td className="px-4 py-3 text-gray-700">
                      {course.category?.name || course.category || '-'}
                    </td>

                    <td className="px-4 py-3 text-gray-700">
                      {course.instructor?.user?.name || course.instructor?.name || course.instructor?.name || course.instructor?.user?.name || course.instructor?.name || course.instructor?.title || course.instructor || '-'}
                    </td>

                    <td className="px-4 py-3 text-gray-700">
                      ${Number(course.price || 0).toFixed(2)}
                    </td>

                    <td className="px-4 py-3">
                      <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-gray-100 text-gray-700 capitalize">
                        {course.level || '-'}
                      </span>
                    </td>

                    <td className="px-4 py-3">
                      <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${course.is_active ? 'bg-primary text-white' : 'bg-red-100 text-red-800'
                        }`}>
                        {course.is_active ? 'Active' : 'Inactive'}
                      </span>
                    </td>

                    <td className="px-4 py-3">
                      <div className="flex items-center justify-end gap-2">
                        <button
                          type="button"
                          onClick={() => handleEditCourse(course)}
                          className="inline-flex items-center gap-1  text-blue-600 rounded-lg hover:bg-blue-100 transition-colors"
                        >
                          <Edit2 className="w-4 h-4" />

                        </button>
                        <button
                          type="button"
                          onClick={() => handleDeleteCourse(course.id)}
                          className="inline-flex items-center gap-1  text-red-600 rounded-lg hover:bg-red-100 transition-colors"
                        >
                          <Trash2 className="w-4 h-4" />

                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {/* Pagination */}
        {pagination && pagination.last_page > 1 && (
          <div className="flex flex-col sm:flex-row justify-between items-center gap-4 pt-6 border-t border-gray-200">
            {/* Per Page Selector */}
            <div className="flex items-center gap-2">
              <span className="text-sm text-gray-600">Show:</span>
              <select
                value={perPage}
                onChange={(e) => handlePerPageChange(Number(e.target.value))}
                className="px-3 py-1 border border-gray-300 rounded-md text-sm focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              >
                <option value={6}>6</option>
                <option value={12}>12</option>
                <option value={24}>24</option>
                <option value={36}>36</option>
              </select>
              <span className="text-sm text-gray-600">per page</span>
            </div>

            {/* Pagination Info */}
            <div className="text-sm text-gray-600">
              Showing {pagination.from || 0} to {pagination.to || 0} of {pagination.total || 0} courses
            </div>

            {/* Page Navigation */}
            <div className="flex items-center gap-2">
              <button
                onClick={() => handlePageChange(currentPage - 1)}
                disabled={currentPage <= 1}
                className="px-3 py-1 border border-gray-300 rounded-md text-sm hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Previous
              </button>

              {/* Page Numbers */}
              <div className="flex items-center gap-1">
                {Array.from({ length: Math.min(5, pagination.last_page || 1) }, (_, i) => {
                  let pageNum;
                  const totalPages = pagination.last_page || 1;

                  if (totalPages <= 5) {
                    pageNum = i + 1;
                  } else {
                    // Center around current page
                    const startPage = Math.max(1, Math.min(currentPage - 2, totalPages - 4));
                    pageNum = startPage + i;
                  }

                  // Only show valid page numbers
                  if (pageNum > totalPages) return null;

                  return (
                    <button
                      key={pageNum}
                      onClick={() => handlePageChange(pageNum)}
                      className={`px-3 py-1 border rounded-md text-sm ${currentPage === pageNum
                        ? 'bg-blue-600 text-white border-blue-600'
                        : 'border-gray-300 hover:bg-gray-50'
                        }`}
                    >
                      {pageNum}
                    </button>
                  );
                })}
              </div>

              <button
                onClick={() => handlePageChange(currentPage + 1)}
                disabled={currentPage >= pagination.last_page}
                className="px-3 py-1 border border-gray-300 rounded-md text-sm hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Next
              </button>
            </div>
          </div>
        )}

        {/* Empty State */}
        {courses.length === 0 && !loading && (
          <div className="text-center py-12">
            <div className="text-gray-400 mb-4">
              <svg
                className="mx-auto h-12 w-12"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={1}
                  d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.747 0 3.332.477 4.5 1.253v13C19.832 18.477 18.247 18 16.5 18c-1.746 0-3.332.477-4.5 1.253"
                />
              </svg>
            </div>
            <h3 className="text-lg font-medium text-gray-900 mb-2">
              No courses found
            </h3>
            <p className="text-gray-500 mb-4">
              Get started by creating your first course.
            </p>
            <Button
              variant="primary"
              text="Create Course"
              onClick={() => {
                if (!isAuthenticated) {
                  alert('Please log in to create courses.');
                  return;
                }
                if (user?.role !== 'admin') {
                  alert('Only administrators can create courses.');
                  return;
                }
                setOpenForm(true);
              }}
              className="bg-blue-600 hover:bg-blue-700 text-white"
            />
          </div>
        )}
      </div>
    </div>
  );
}

import React, { useState, useEffect, useRef } from "react";
import { useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import Sidebar from "./Sidebar";
import StudentTable from "./StudentsTable";
import EnrolledStudentsTable from "./EnrolledStudentsTable.jsx";
import { apiClient } from '../../../src/api/index.js';

import {
  X,
  Menu,
  BookOpen,
  Users,
  ShoppingBag,
  BarChart3,
  TrendingUp,
  Download,
  UserPlus,
  Bell,
  Mail,
  Search,
  ChevronDown,
  Clock,
  UserCheck,
  AlertCircle,
  CheckCircle,
  MoreVertical,
  LogOut,
} from "lucide-react";
import { useDispatch } from 'react-redux';
import { logoutAsync } from '../../../slices/AuthSlice';
import AllCourses from "./AllCourses";
import TeachersTable from "./TeacherTable.jsx";
import CourseRequests from "./CourseRequests";

const Card = ({ children, className = "", hover = false }) => (
  <div
    className={`bg-white rounded-2xl p-2 sm:p-4 shadow-sm border border-gray-100 transition-all duration-300
      ${hover ? "hover:shadow-lg hover:border-blue-100" : ""} ${className}`}
  >
    {children}
  </div>
);

function NotificationSidebar({ isOpen, onClose, pendingRequests, onApprove, onReject }) {
  return (
    <>
      {isOpen && (
        <div
          className="fixed inset-0 bg-black/40 backdrop-blur-sm z-40 transition-opacity duration-300 ease-out"
          onClick={onClose}
        />
      )}
      <div
        className={`fixed top-0 right-0 h-full w-full sm:w-[420px] bg-white shadow-xl z-50 transform transition-all duration-300 ease-out ${isOpen ? "translate-x-0" : "translate-x-full"
          }`}
      >
        {/* Sidebar content same as your original code */}
        <div className="sticky top-0 bg-white border-b border-gray-100 px-6 py-5">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <div className="relative">
                <div className="p-2 bg-blue-50 rounded-lg">
                  <Bell className="text-blue-600" size={22} />
                </div>
                {pendingRequests.length > 0 && (
                  <span className="absolute -top-1 -right-1 w-5 h-5 bg-red-500 text-white text-xs rounded-full flex items-center justify-center font-medium shadow-sm">
                    {pendingRequests.length}
                  </span>
                )}
              </div>
              <div>
                <h2 className="text-xl font-bold text-gray-900">Notifications</h2>
                <p className="text-sm text-gray-500 mt-0.5">
                  {pendingRequests.length} pending requests
                </p>
              </div>
            </div>
            <button
              onClick={onClose}
              className="p-2 hover:bg-gray-50 rounded-lg transition-colors text-gray-500 hover:text-gray-700"
            >
              <X size={20} />
            </button>
          </div>
          {/* Tabs and content same as your original code */}
        </div>
      </div>
    </>
  );
}

function DashboardCards() {
  const [stats, setStats] = useState([]);
  const [recentActivities, setRecentActivities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        const response = await apiClient.getAdminStats();

        // Transform stats data for display
        const transformedStats = [
          {
            title: "Total Courses",
            value: response.total_courses?.toString() || "0",
            change: "+12%",
            icon: <BookOpen />
          },
          {
            title: "Active Students",
            value: response.total_students?.toString() || "0",
            change: "+8%",
            icon: <Users />
          },
          {
            title: "Total Enrollments",
            value: response.total_enrollments?.toString() || "0",
            change: "+23%",
            icon: <ShoppingBag />
          },
          {
            title: "Pending Instructors",
            value: response.pending_instructors_count?.toString() || "0",
            change: "0",
            icon: <UserPlus />
          },
          {
            title: "Pending Students",
            value: response.pending_requests_count?.toString() || "0",
            change: "+5%",
            icon: <Clock />
          }
        ];


        // Transform recent activities
        const transformedActivities = response.recent_activities?.map(activity => ({
          user: activity.user?.name || "Unknown User",
          action: "enrolled in",
          course: activity.course?.title || "Unknown Course",
          time: activity.created_at ? new Date(activity.created_at).toLocaleString() : "Recently"
        })) || [];

        setStats(transformedStats);
        setRecentActivities(transformedActivities);
        setError(null);
      } catch (err) {
        console.error('Failed to fetch dashboard data:', err);
        console.error('Error details:', err.message);
        setError(`Failed to load dashboard data: ${err.message}`);
        // Fallback to mock data if API fails
        setStats([
          { title: "Total Courses", value: "82", change: "+12%", icon: <BookOpen /> },
          { title: "Active Students", value: "1,240", change: "+8%", icon: <Users /> },
          { title: "Total Orders", value: "350", change: "+23%", icon: <ShoppingBag /> },
          { title: "Course Rating", value: "4.8/5", change: "+0.2", icon: <TrendingUp /> }
        ]);
        setRecentActivities([
          { user: "John Doe", action: "purchased", course: "Web Dev Bootcamp", time: "2 min ago" },
          { user: "Sarah Smith", action: "completed", course: "React Mastery", time: "1 hour ago" },
          { user: "Mike Johnson", action: "enrolled in", course: "JS Advanced", time: "3 hours ago" },
          { user: "Emily Davis", action: "rated", course: "React Mastery", time: "5 hours ago" }
        ]);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  if (loading) {
    return (
      <div className="space-y-6">
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
          {[...Array(4)].map((_, index) => (
            <Card key={index}>
              <div className="animate-pulse">
                <div className="h-4 bg-gray-200 rounded w-3/4 mb-2"></div>
                <div className="h-8 bg-gray-200 rounded w-1/2 mb-2"></div>
                <div className="h-4 bg-gray-200 rounded w-1/4"></div>
              </div>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="space-y-6">
        <div className="text-center py-8">
          <p className="text-red-600">{error}</p>
          <button
            onClick={() => window.location.reload()}
            className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-lg"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Stats Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-6">
        {stats.map((stat, index) => (
          <Card key={index} hover>
            <div className=" flex justify-between items-start">
              <div>
                <p className="text-gray-500 text-sm">{stat.title}</p>
                <p className="text-2xl font-bold mt-1">{stat.value}</p>
                <p className="text-primary-dark text-sm mt-2 font-medium">{stat.change}</p>
              </div>
              <div className="p-2 bg-primary rounded-xl text-white">{stat.icon}</div>
            </div>
          </Card>
        ))}
      </div>

      {/* Charts & Recent Activities */}
      <div className="grid grid-cols-1 xl:grid-cols-3 gap-6">
        <Card className="xl:col-span-2">
          <div className="flex justify-between items-center mb-4">
            <h3 className="font-bold text-lg text-gray-900">Revenue Overview</h3>
            <button className="flex items-center gap-2 px-4 py-2 bg-primary text-white text-sm font-medium rounded-lg hover:bg-primary-dark cursor-pointer transition-colors">
              <Download size={16} />
              Export
            </button>
          </div>
          <div className="h-64 bg-gradient-to-br from-blue-50 to-blue-100/50 rounded-xl flex flex-col items-center justify-center">
            <BarChart3 size={48} className="text-primary-dark mb-3" />
            <p className="text-gray-600">Revenue chart visualization</p>
            <h2 className="text-2xl font-bold mt-2 text-gray-900">$24,580</h2>
            <p className="text-primary-dark text-sm font-medium mt-1">+18% from last month</p>
          </div>
        </Card>

        <Card>
          <h3 className="font-bold text-lg text-gray-900 mb-4">Recent Activities</h3>

          <div className="space-y-4">
            {recentActivities.length > 0 ? (
              recentActivities.slice(0, 3).map((act, i) => (
                <div
                  key={i}
                  className="flex items-start gap-3 p-3 rounded-xl hover:bg-gray-50 transition-colors"
                >
                  <div className="w-10 h-10 bg-primary-dark rounded-full flex items-center justify-center text-white flex-shrink-0">
                    <UserPlus size={18} />
                  </div>

                  <div className="min-w-0">
                    <p className="text-sm text-gray-800">
                      <span className="font-medium">{act.user}</span>{" "}
                      {act.action}{" "}
                      <span className="font-semibold text-gray-900">{act.course}</span>
                    </p>
                    <p className="text-xs text-gray-500 mt-1">{act.time}</p>
                  </div>
                </div>
              ))
            ) : (
              <p className="text-gray-500 text-center py-4">No recent activities</p>
            )}
          </div>
        </Card>

      </div>
    </div>
  );
}

export default function DashboardView() {
  const [open, setOpen] = useState(true);
  const [active, setActive] = useState("Dashboard");
  const [notificationOpen, setNotificationOpen] = useState(false);
  const [profileDropdown, setProfileDropdown] = useState(false);
  const dropdownRef = useRef(null);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { isAuthenticated, loading } = useSelector(state => state.auth);

  const handleLogout = () => {
    dispatch(logoutAsync());
  };

  useEffect(() => {
    if (!isAuthenticated && !loading) {
      navigate('/login');
    }
  }, [isAuthenticated, loading, navigate]);

  // Fetch pending instructor requests
  useEffect(() => {
    const fetchPendingRequests = async () => {
      try {
        const response = await apiClient.getInstructors({ approval_status: 'submitted' });
        const instructors = response.data || [];
        const formattedRequests = instructors.map(instructor => ({
          id: instructor.id,
          name: instructor.name,
          email: instructor.email || 'N/A', // Assuming email is included
          time: instructor.created_at ? new Date(instructor.created_at).toLocaleString() : 'Recently',
          subject: instructor.specialization ? instructor.specialization.join(', ') : 'General',
          experience: instructor.experience ? `${instructor.experience.length} items` : 'N/A',
        }));
        setPendingRequests(formattedRequests);
      } catch (error) {
        console.error('Error fetching pending requests:', error);
      }
    };

    fetchPendingRequests();
  }, []);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setProfileDropdown(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // State for pending instructor requests
  const [pendingRequests, setPendingRequests] = useState([]);

  const [pendingCourseRequestsCount, setPendingCourseRequestsCount] = useState(0);

  useEffect(() => {
    const fetchPendingCourseRequestsCount = async () => {
      try {
        const response = await apiClient.getCourseRequests({ status: 'pending', per_page: 1 });
        const meta = response?.meta;
        const total = typeof meta?.total === 'number' ? meta.total : undefined;

        if (typeof total === 'number') {
          setPendingCourseRequestsCount(total);
          return;
        }

        const data = response?.data;
        const items = Array.isArray(data) ? data : (Array.isArray(data?.data) ? data.data : []);
        setPendingCourseRequestsCount(items.length);
      } catch (error) {
        console.error('Error fetching pending course requests count:', error);
        setPendingCourseRequestsCount(0);
      }
    };

    fetchPendingCourseRequestsCount();
  }, []);

  const handleApprove = async (id) => {
    try {
      await apiClient.approveInstructor(id);
      setPendingRequests((prev) =>
        prev.filter((request) => request.id !== id)
      );
    } catch (error) {
      console.error('Error approving instructor:', error);
      alert('Failed to approve instructor.');
    }
  };

  const handleReject = async (id) => {
    try {
      await apiClient.rejectInstructor(id);
      setPendingRequests((prev) =>
        prev.filter((request) => request.id !== id)
      );
    } catch (error) {
      console.error('Error rejecting instructor:', error);
      alert('Failed to reject instructor.');
    }
  };

  return (
    <div className="flex bg-gray-50 min-h-screen">
      <Sidebar open={open} setOpen={setOpen} active={active} setActive={setActive} />

      <main className={`flex-1 p-4 sm:p-6 ml-20 lg:ml-0 transition-all duration-500`}>
        {/* Top Bar */}
        <div className="flex items-center justify-between mb-6">
          <h1 className="text-2xl sm:text-3xl font-bold text-gray-900">{active}</h1>

          {/* Right Actions */}
          <div className="hidden sm:flex items-center gap-4">

            {/* Notification */}
            <button
              className="relative p-2 bg-white rounded-lg shadow-sm border border-gray-300 hover:bg-gray-50 transition-colors"
              onClick={() => setNotificationOpen(true)}
            >
              <Bell size={18} className="text-gray-700" />
              {(pendingRequests.length + pendingCourseRequestsCount) > 0 && (
                <span className="absolute -top-1 -right-1 w-4 h-4 bg-red-500 rounded-full flex items-center justify-center">
                  <span className="text-[10px] text-white font-medium">{pendingRequests.length + pendingCourseRequestsCount}</span>
                </span>
              )}
            </button>

            {/* Messages */}
            <button className="relative p-2 bg-white rounded-lg shadow-sm border border-gray-300 hover:bg-gray-50 transition-colors">
              <Mail size={18} className="text-gray-700" />
              <span className="absolute -top-1 -right-1 w-4 h-4 bg-primary-dark rounded-full flex items-center justify-center">
                <span className="text-[10px] text-white font-medium">3</span>
              </span>
            </button>

            {/* User Profile */}
            <div className="relative" ref={dropdownRef}>
              <button
                onClick={() => setProfileDropdown(!profileDropdown)}
                className="flex items-center gap-3 pl-1 p-2 cursor-pointer rounded-lg hover:bg-gray-50 transition-colors"
              >
                <div className="flex items-center gap-2">
                  <img
                    src="https://ui-avatars.com/api/?name=Admin&background=0D8ABC&color=fff"
                    className="w-8 h-8 rounded-full border-2 border-white shadow-sm"
                    alt="Admin"
                  />
                  <div className="hidden md:block text-left">
                    <p className="text-sm font-medium text-gray-900">Admin</p>
                    <p className="text-xs text-gray-500">Super Admin</p>
                  </div>
                </div>
                <ChevronDown size={16} className="text-gray-500" />
              </button>

              {profileDropdown && (
                <div className="absolute right-0 top-full mt-2 w-36 bg-white rounded-lg shadow-lg border border-gray-200 z-50">
                  <div className=" ">
                    <button
                      onClick={() => {
                        handleLogout();
                        setProfileDropdown(false);
                      }}
                      className="flex items-center w-full rounded-lg px-4 py-2 w-full text-sm text-primary hover:bg-red-500 hover:text-white cursor-pointer"
                    >
                      <LogOut className="mr-3" size={16} />
                      Logout
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Mobile Menu Button */}
          <button
            className="sm:hidden p-2 bg-white shadow-sm rounded-lg border border-gray-300"
            onClick={() => setOpen(!open)}
          >
            {open ? <X size={20} /> : <Menu size={20} />}
          </button>
        </div>

        {/* Content Area */}
        <div className="space-y-6">
          {active === "Dashboard" && <DashboardCards />}
          {active === "Courses" && <AllCourses />}
          {active === "Course Requests" && <CourseRequests />}
          {active === "Students" && <StudentTable />}
          {active === "Teachers" && <TeachersTable />}
          {active === "Enrolled Students" && <EnrolledStudentsTable />}
          {/* {active === "Pending Approvals" && <PendingApprovals />} */}


        </div>
      </main>

      {/* Notification Sidebar */}
      <NotificationSidebar
        isOpen={notificationOpen}
        onClose={() => setNotificationOpen(false)}
        pendingRequests={pendingRequests}
        onApprove={handleApprove}
        onReject={handleReject}
      />
    </div>
  );
}

import React, { useEffect, useState } from "react";
import { Edit2, Trash2, CheckCircle2, XCircle } from "lucide-react";
import { apiClient } from "../../../src/api/index.js";

export default function StudentsTable() {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loadingIds, setLoadingIds] = useState([]); // Track loading for specific users

  // ================= FETCH STUDENTS =================
  const fetchUsers = async () => {
    try {
      const res = await apiClient.getUsers();
      const allUsers = res.data || [];
      const students = allUsers.filter((u) => u.role === "student");
      setUsers(students);
    } catch (err) {
      console.error("Error fetching students:", err);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  // ================= APPROVE =================
  const handleApprove = async (userId) => {
    if (!window.confirm("Approve this student?")) return;

    setLoadingIds((prev) => [...prev, userId]);
    try {
      await apiClient.approveUser(userId);

      // Optimistic update
      setUsers((prev) =>
        prev.map((u) => (u.id === userId ? { ...u, is_active: true } : u))
      );
    } catch (err) {
      alert("Approval failed");
    } finally {
      setLoadingIds((prev) => prev.filter((id) => id !== userId));
    }
  };

  // ================= REJECT =================
  const handleReject = async (userId) => {
    if (!window.confirm("Reject this student? This will delete account.")) return;

    setLoadingIds((prev) => [...prev, userId]);
    try {
      await apiClient.deleteUser(userId);

      // Optimistic removal
      setUsers((prev) => prev.filter((u) => u.id !== userId));
    } catch (err) {
      alert("Rejection failed");
    } finally {
      setLoadingIds((prev) => prev.filter((id) => id !== userId));
    }
  };

  // ================= DELETE =================
  const handleDelete = async (userId) => {
    if (!window.confirm("Delete this student?")) return;

    setLoadingIds((prev) => [...prev, userId]);
    try {
      await apiClient.deleteUser(userId);

      // Optimistic removal
      setUsers((prev) => prev.filter((u) => u.id !== userId));
    } catch (err) {
      alert("Delete failed");
    } finally {
      setLoadingIds((prev) => prev.filter((id) => id !== userId));
    }
  };

  // ================= FILTER =================
  const filteredUsers = users.filter(
    (u) =>
      u.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      u.email.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="rounded-xl shadow-sm border-gray-200 overflow-hidden">
      {/* HEADER */}
      <div className="px-6 flex flex-col md:flex-row justify-between items-start md:items-center py-5 border-b border-gray-200 bg-primary">
        <h2 className="text-3xl text-white font-bold mb-3 md:mb-0">
          Students Management
        </h2>

        {/* Search */}
        <div className="relative w-full md:w-72">
          <input
            type="text"
            className="w-full pl-10 pr-4 py-2.5 rounded-lg text-sm 
                       bg-white/95 text-gray-800 
                       placeholder-gray-400
                       focus:outline-none focus:ring-2 focus:ring-white/70
                       transition"
            placeholder="Search students by name or email..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />

          <svg
            className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400"
            width="16"
            height="16"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
            viewBox="0 0 24 24"
          >
            <circle cx="11" cy="11" r="8" />
            <line x1="21" y1="21" x2="16.65" y2="16.65" />
          </svg>
        </div>
      </div>

      {/* TABLE */}
      <div className="overflow-x-auto">
        <table className="w-full text-sm">
          <thead>
            <tr className="bg-gray-50 border-b border-gray-200 ">
              {["Student", "Email", "Status", "Approval", "Actions"].map((h) => (
                <th
                  key={h}
                  className="px-4 py-3 text-left text-xs font-semibold text-gray-600 uppercase"
                >
                  {h}
                </th>
              ))}
            </tr>
          </thead>

          <tbody className="divide-y ">
            {filteredUsers.map((u) => (
              <tr key={u.id} className="hover:bg-gray-50 border-gray-200">
                <td className="px-4 py-3">
                  <p className="font-medium">{u.name}</p>
                </td>

                <td className="px-4 py-3">{u.email}</td>

                <td className="px-4 py-3">
                  {u.is_active ? (
                    <span className="px-2 py-1 text-xs bg-primary text-white rounded-full">
                      Active
                    </span>
                  ) : (
                    <span className="px-2 py-1 text-xs bg-yellow-100 text-yellow-700 rounded-full">
                      Pending
                    </span>
                  )}
                </td>

                {/* APPROVAL */}
                <td className="px-4 py-3">
                  {!u.is_active ? (
                    <div className="flex gap-3">
                      <button
                        disabled={loadingIds.includes(u.id)}
                        onClick={() => handleApprove(u.id)}
                        className={`text-green-600 ${loadingIds.includes(u.id) ? "opacity-50 cursor-not-allowed" : ""
                          }`}
                      >
                        <CheckCircle2 size={18} />
                      </button>
                      <button
                        disabled={loadingIds.includes(u.id)}
                        onClick={() => handleReject(u.id)}
                        className={`text-red-600 ${loadingIds.includes(u.id) ? "opacity-50 cursor-not-allowed" : ""
                          }`}
                      >
                        <XCircle size={18} />
                      </button>
                    </div>
                  ) : (
                    <span className="text-xs text-primary font-medium">Approved</span>
                  )}
                </td>

                {/* ACTIONS */}
                <td className="px-4 py-3 flex gap-3">
                  <button className="text-primary-dark">
                    <Edit2 size={18} />
                  </button>
                  <button
                    onClick={() => handleDelete(u.id)}
                    disabled={loadingIds.includes(u.id)}
                    className={`text-red-600 ${loadingIds.includes(u.id) ? "opacity-50 cursor-not-allowed" : ""
                      }`}
                  >
                    <Trash2 size={18} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {filteredUsers.length === 0 && (
          <p className="text-center py-10 text-gray-500">No students found</p>
        )}
      </div>
    </div>
  );
}

